INSERT INTO `wp_term_taxonomy` (`term_taxonomy_id`, `term_id`, `taxonomy`, `description`, `parent`, `count`) VALUES
(2, 2, 'wp_theme', '', 0, 1),
(3, 3, 'category', '', 0, 0),
(4, 4, 'category', 'Частный дом – это индивидуально-определенное здание, состоящее из комнат, а также помещений вспомогательного использования, предназначенных для удовлетворения гражданами бытовых и иных нужд, связанных с их проживанием в нем', 3, 15),
(5, 5, 'category', 'Кварти́ра — один из видов жилого помещения, состоящий из одной или нескольких смежных комнат а также в отдельных случаях с отдельным наружным выходом, составляющее отдельную часть дома.', 3, 15),
(6, 6, 'category', 'Офис или конто́ра — помещение, здание, комплекс зданий, в котором работают служащие предприятия. В офисе принимают клиентов, хранят и обрабатывают документы, архивы и тому подобное.', 3, 15),
(7, 7, 'wp_theme', '', 0, 1),
(8, 8, 'nav_menu', '', 0, 3);
